import React, { useState } from 'react';
import './MetricCard.css';

function MetricCard({ 
  label, 
  kpiKey,
  postLift, 
  postTY, 
  postLY, 
  preLift,
  preTY,
  preLY,
  compLift,
  format,
  isSelected,
  onSelect
}) {
  const [isExpanded, setIsExpanded] = useState(false);
  
  const isPositive = postLift > 0;
  const isNegative = postLift < 0;
  const isNeutral = postLift === 0;

  const formatValue = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    
    if (format === 'currency') {
      if (Math.abs(value) >= 1000000) {
        return `$${(value / 1000000).toFixed(2)}M`;
      }
      if (Math.abs(value) >= 1000) {
        return `$${(value / 1000).toFixed(1)}K`;
      }
      return `$${value.toFixed(2)}`;
    }
    if (format === 'percentage') {
      return `${value.toFixed(2)}%`;
    }
    if (Math.abs(value) >= 1000000) {
      return `${(value / 1000000).toFixed(2)}M`;
    }
    if (Math.abs(value) >= 1000) {
      return `${(value / 1000).toFixed(1)}K`;
    }
    return value.toLocaleString();
  };

  const formatLift = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    const sign = value > 0 ? '+' : '';
    return `${sign}${value.toFixed(2)}%`;
  };

  const getLiftClass = (value) => {
    if (value > 0) return 'positive';
    if (value < 0) return 'negative';
    return 'neutral';
  };

  const getDirectionIcon = () => {
    if (isPositive) {
      return (
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
          <polyline points="18 15 12 9 6 15"></polyline>
        </svg>
      );
    }
    if (isNegative) {
      return (
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
          <polyline points="6 9 12 15 18 9"></polyline>
        </svg>
      );
    }
    return (
      <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
        <line x1="5" y1="12" x2="19" y2="12"></line>
      </svg>
    );
  };

  const handleCardClick = () => {
    if (onSelect) {
      onSelect(kpiKey);
    }
  };

  const handleExpandClick = (e) => {
    e.stopPropagation();
    setIsExpanded(!isExpanded);
  };

  return (
    <div 
      className={`metric-card glass-heavy 
        ${isPositive ? 'positive' : ''} 
        ${isNegative ? 'negative' : ''} 
        ${isNeutral ? 'neutral' : ''}
        ${isSelected ? 'selected' : ''}
        ${isExpanded ? 'expanded' : ''}`}
      onClick={handleCardClick}
    >
      <div className="metric-header">
        <span className="metric-label">{label}</span>
        <button 
          className="expand-btn"
          onClick={handleExpandClick}
          aria-label={isExpanded ? 'Collapse details' : 'Expand details'}
        >
          <svg 
            width="16" 
            height="16" 
            viewBox="0 0 24 24" 
            fill="none" 
            stroke="currentColor" 
            strokeWidth="2" 
            strokeLinecap="round" 
            strokeLinejoin="round"
            style={{ transform: isExpanded ? 'rotate(180deg)' : 'rotate(0deg)', transition: 'transform 0.2s ease' }}
          >
            <polyline points="6 9 12 15 18 9"></polyline>
          </svg>
        </button>
      </div>
      
      <div className="metric-main">
        <div className={`metric-direction ${isPositive ? 'up' : ''} ${isNegative ? 'down' : ''}`}>
          {getDirectionIcon()}
        </div>
        <div className="metric-lift">
          <span className="lift-value">{formatLift(postLift)}</span>
          <span className="lift-label">Post Lift vs LY</span>
        </div>
      </div>

      <div className="metric-summary">
        <div className="summary-item">
          <span className="summary-label">TY</span>
          <span className="summary-value">{formatValue(postTY)}</span>
        </div>
        <div className="summary-divider"></div>
        <div className="summary-item">
          <span className="summary-label">LY</span>
          <span className="summary-value">{formatValue(postLY)}</span>
        </div>
      </div>

      {/* Expanded Details */}
      <div className="metric-expanded">
        <div className="expanded-section">
          <div className="expanded-header">Pre-Launch Period</div>
          <div className="expanded-row">
            <div className="expanded-item">
              <span className="expanded-label">TY</span>
              <span className="expanded-value">{formatValue(preTY)}</span>
            </div>
            <div className="expanded-item">
              <span className="expanded-label">LY</span>
              <span className="expanded-value">{formatValue(preLY)}</span>
            </div>
            <div className="expanded-item">
              <span className="expanded-label">Lift</span>
              <span className={`expanded-value ${getLiftClass(preLift)}`}>{formatLift(preLift)}</span>
            </div>
          </div>
        </div>
        
        <div className="expanded-section comp-section">
          <div className="comp-lift-container">
            <span className="comp-label">Incremental Impact (Comp Lift)</span>
            <span className={`comp-value ${getLiftClass(compLift)}`}>{formatLift(compLift)}</span>
          </div>
          <p className="comp-description">Post Lift minus Pre Lift - measures the incremental impact of the launch</p>
        </div>
      </div>

      {isSelected && <div className="selected-indicator"></div>}
    </div>
  );
}

export default MetricCard;
