import React, { useEffect } from 'react';
import './MetricModal.css';

function MetricModal({ data, onClose }) {
  const {
    label,
    postLift,
    postTY,
    postLY,
    preLift,
    preTY,
    preLY,
    compLift,
    format
  } = data;

  const isPositive = (val) => val > 0;
  const isNegative = (val) => val < 0;

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') onClose();
    };
    document.addEventListener('keydown', handleEscape);
    document.body.style.overflow = 'hidden';
    
    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = '';
    };
  }, [onClose]);

  const formatValue = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    
    if (format === 'currency') {
      if (Math.abs(value) >= 1000000) {
        return `$${(value / 1000000).toFixed(2)}M`;
      }
      if (Math.abs(value) >= 1000) {
        return `$${(value / 1000).toFixed(1)}K`;
      }
      return `$${value.toFixed(2)}`;
    }
    if (format === 'percentage') {
      return `${value.toFixed(2)}%`;
    }
    if (Math.abs(value) >= 1000000) {
      return `${(value / 1000000).toFixed(2)}M`;
    }
    if (Math.abs(value) >= 1000) {
      return `${(value / 1000).toFixed(1)}K`;
    }
    return value.toLocaleString();
  };

  const formatLift = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    const sign = value > 0 ? '+' : '';
    return `${sign}${value.toFixed(2)}%`;
  };

  const getLiftClass = (value) => {
    if (isPositive(value)) return 'positive';
    if (isNegative(value)) return 'negative';
    return '';
  };

  const getArrow = (value) => {
    if (isPositive(value)) return '↑';
    if (isNegative(value)) return '↓';
    return '→';
  };

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div className="modal-backdrop" onClick={handleBackdropClick}>
      <div className="modal-container">
        <div className="modal-header">
          <h2>{label}</h2>
          <button className="modal-close" onClick={onClose}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <line x1="18" y1="6" x2="6" y2="18"></line>
              <line x1="6" y1="6" x2="18" y2="18"></line>
            </svg>
          </button>
        </div>

        <div className="modal-body">
          {/* Post Period */}
          <div className="period-section">
            <div className="period-header">
              <h3>Post-Launch Period</h3>
              <span className={`period-lift ${getLiftClass(postLift)}`}>
                {getArrow(postLift)} {formatLift(postLift)}
              </span>
            </div>
            <div className="period-data">
              <div className="data-item">
                <span className="data-label">This Year</span>
                <span className="data-value">{formatValue(postTY)}</span>
              </div>
              <div className="data-item">
                <span className="data-label">Last Year</span>
                <span className="data-value muted">{formatValue(postLY)}</span>
              </div>
            </div>
          </div>

          {/* Pre Period */}
          <div className="period-section">
            <div className="period-header">
              <h3>Pre-Launch Period</h3>
              <span className={`period-lift ${getLiftClass(preLift)}`}>
                {getArrow(preLift)} {formatLift(preLift)}
              </span>
            </div>
            <div className="period-data">
              <div className="data-item">
                <span className="data-label">This Year</span>
                <span className="data-value">{formatValue(preTY)}</span>
              </div>
              <div className="data-item">
                <span className="data-label">Last Year</span>
                <span className="data-value muted">{formatValue(preLY)}</span>
              </div>
            </div>
          </div>

          {/* Comp Lift */}
          <div className="comp-section">
            <div className="comp-header">
              <h3>Incremental Impact</h3>
            </div>
            <div className={`comp-result ${getLiftClass(compLift)}`}>
              <span className="comp-arrow">{getArrow(compLift)}</span>
              <span className="comp-value">{formatLift(compLift)}</span>
            </div>
            <p className="comp-explanation">
              Post Lift ({formatLift(postLift)}) minus Pre Lift ({formatLift(preLift)}) shows the incremental change attributable to the launch.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MetricModal;
