import React, { useState, useEffect } from 'react';
import api from '../utils/api';
import './SendReportModal.css';

function SendReportModal({ useCase, period, periodLabel, onClose }) {
  const [stakeholders, setStakeholders] = useState([]);
  const [selectedRecipients, setSelectedRecipients] = useState([]);
  const [customEmail, setCustomEmail] = useState('');
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);
  const [sent, setSent] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchStakeholders();
  }, [useCase]);

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') onClose();
    };
    document.addEventListener('keydown', handleEscape);
    document.body.style.overflow = 'hidden';
    
    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = '';
    };
  }, [onClose]);

  const fetchStakeholders = async () => {
    try {
      const response = await api.get('/api/stakeholders', {
        params: { use_case: useCase }
      });
      const emails = response.data.stakeholders || [];
      setStakeholders(emails);
      setSelectedRecipients(emails); // Select all by default
    } catch (err) {
      console.error('Error fetching stakeholders:', err);
    } finally {
      setLoading(false);
    }
  };

  const toggleRecipient = (email) => {
    setSelectedRecipients(prev => 
      prev.includes(email)
        ? prev.filter(e => e !== email)
        : [...prev, email]
    );
  };

  const addCustomEmail = () => {
    const email = customEmail.trim();
    if (email && email.includes('@') && !selectedRecipients.includes(email)) {
      setSelectedRecipients(prev => [...prev, email]);
      setCustomEmail('');
    }
  };

  const removeRecipient = (email) => {
    setSelectedRecipients(prev => prev.filter(e => e !== email));
  };

  const handleSend = async () => {
    if (selectedRecipients.length === 0) {
      setError('Please select at least one recipient');
      return;
    }

    setSending(true);
    setError(null);

    try {
      await api.post('/api/report/send', {
        use_case: useCase,
        period: period,
        recipients: selectedRecipients
      });
      setSent(true);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to send report');
    } finally {
      setSending(false);
    }
  };

  const handlePreview = () => {
    window.open(`/api/report/preview?use_case=${encodeURIComponent(useCase)}&period=${period}`, '_blank');
  };

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) onClose();
  };

  if (sent) {
    return (
      <div className="modal-backdrop" onClick={handleBackdropClick}>
        <div className="send-modal success-state">
          <div className="success-icon">
            <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
              <polyline points="22 4 12 14.01 9 11.01"></polyline>
            </svg>
          </div>
          <h2>Report Sent</h2>
          <p>The report has been sent to {selectedRecipients.length} recipient{selectedRecipients.length !== 1 ? 's' : ''}.</p>
          <button className="btn-primary" onClick={onClose}>Done</button>
        </div>
      </div>
    );
  }

  return (
    <div className="modal-backdrop" onClick={handleBackdropClick}>
      <div className="send-modal">
        <div className="modal-header">
          <div className="header-content">
            <h2>Send Report</h2>
            <p className="header-subtitle">{useCase}</p>
          </div>
          <button className="close-btn" onClick={onClose}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <line x1="18" y1="6" x2="6" y2="18"></line>
              <line x1="6" y1="6" x2="18" y2="18"></line>
            </svg>
          </button>
        </div>

        <div className="modal-body">
          <div className="report-preview-section">
            <div className="preview-info">
              <span className="preview-label">Report Period</span>
              <span className="preview-value">{periodLabel}</span>
            </div>
            <button className="preview-btn" onClick={handlePreview}>
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"></path>
                <circle cx="12" cy="12" r="3"></circle>
              </svg>
              Preview PDF
            </button>
          </div>

          <div className="recipients-section">
            <label className="section-label">Recipients</label>
            
            {loading ? (
              <div className="loading-recipients">Loading stakeholders...</div>
            ) : (
              <>
                {stakeholders.length > 0 && (
                  <div className="stakeholders-list">
                    <span className="list-label">Stakeholders from config:</span>
                    {stakeholders.map(email => (
                      <label key={email} className="recipient-checkbox">
                        <input
                          type="checkbox"
                          checked={selectedRecipients.includes(email)}
                          onChange={() => toggleRecipient(email)}
                        />
                        <span className="checkbox-custom"></span>
                        <span className="recipient-email">{email}</span>
                      </label>
                    ))}
                  </div>
                )}

                <div className="add-recipient">
                  <input
                    type="email"
                    placeholder="Add another email address"
                    value={customEmail}
                    onChange={(e) => setCustomEmail(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && addCustomEmail()}
                  />
                  <button 
                    className="add-btn" 
                    onClick={addCustomEmail}
                    disabled={!customEmail.includes('@')}
                  >
                    Add
                  </button>
                </div>

                {selectedRecipients.length > 0 && (
                  <div className="selected-recipients">
                    <span className="list-label">Will send to:</span>
                    <div className="recipient-tags">
                      {selectedRecipients.map(email => (
                        <span key={email} className="recipient-tag">
                          {email}
                          <button onClick={() => removeRecipient(email)}>
                            <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                              <line x1="18" y1="6" x2="6" y2="18"></line>
                              <line x1="6" y1="6" x2="18" y2="18"></line>
                            </svg>
                          </button>
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </>
            )}
          </div>

          {error && (
            <div className="error-message">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <circle cx="12" cy="12" r="10"></circle>
                <line x1="15" y1="9" x2="9" y2="15"></line>
                <line x1="9" y1="9" x2="15" y2="15"></line>
              </svg>
              {error}
            </div>
          )}
        </div>

        <div className="modal-footer">
          <button className="btn-secondary" onClick={onClose}>Cancel</button>
          <button 
            className="btn-primary" 
            onClick={handleSend}
            disabled={sending || selectedRecipients.length === 0}
          >
            {sending ? (
              <>
                <span className="spinner"></span>
                Sending...
              </>
            ) : (
              <>
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <line x1="22" y1="2" x2="11" y2="13"></line>
                  <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
                </svg>
                Send Report
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
}

export default SendReportModal;
