import React, { useState, useEffect, useCallback } from 'react';
import Header from './Header';
import MetricCard from './MetricCard';
import MetricModal from './MetricModal';
import SendReportModal from './SendReportModal';
import KPIChart from './KPIChart';
import AdvancedAnalysis from './AdvancedAnalysis';
import DataUpload from './DataUpload';
import api from '../utils/api';
import './Dashboard.css';

function Dashboard() {
  const [activeView, setActiveView] = useState('overview');
  const [analysisData, setAnalysisData] = useState([]);
  const [dailyData, setDailyData] = useState([]);
  const [useCases, setUseCases] = useState([]);
  const [useCaseConfigs, setUseCaseConfigs] = useState([]);
  const [selectedUseCase, setSelectedUseCase] = useState('');
  const [selectedKPI, setSelectedKPI] = useState('visits');
  const [chartType, setChartType] = useState('area');
  const [selectedPeriod, setSelectedPeriod] = useState('all');
  const [loading, setLoading] = useState(true);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [hasData, setHasData] = useState(false);
  const [launchDate, setLaunchDate] = useState(null);
  const [totalPostDays, setTotalPostDays] = useState(0);
  const [actualPeriodDays, setActualPeriodDays] = useState(0);
  const [modalData, setModalData] = useState(null);
  const [showSendModal, setShowSendModal] = useState(false);

  const kpiConfig = {
    visits: { label: 'Visits', shortLabel: 'Visits', format: 'number' },
    orders: { label: 'Orders', shortLabel: 'Orders', format: 'number' },
    revenue: { label: 'Revenue', shortLabel: 'Revenue', format: 'currency' },
    cvr: { label: 'Conversion Rate', shortLabel: 'CVR', format: 'percentage' },
    aov: { label: 'Avg Order Value', shortLabel: 'AOV', format: 'currency' },
    rpv: { label: 'Revenue Per Visit', shortLabel: 'RPV', format: 'currency' }
  };

  const periodOptions = [
    { value: '7', label: '7D' },
    { value: '30', label: '30D' },
    { value: '60', label: '60D' },
    { value: '90', label: '90D' },
    { value: 'all', label: 'All' }
  ];

  const fetchDataStatus = useCallback(async () => {
    try {
      const response = await api.get('/api/data/status');
      setHasData(response.data.has_data);
      setLastUpdated(response.data.last_updated);
      setUseCaseConfigs(response.data.configs || []);
      
      if (response.data.use_cases?.length > 0) {
        setUseCases(response.data.use_cases);
        if (!selectedUseCase) {
          setSelectedUseCase(response.data.use_cases[0]);
          
          const config = response.data.configs?.find(c => c.use_case === response.data.use_cases[0]);
          if (config) {
            setLaunchDate(config.launch_date);
          }
        }
      }
    } catch (err) {
      console.error('Error fetching data status:', err);
    } finally {
      setLoading(false);
    }
  }, [selectedUseCase]);

  const fetchAnalysisData = useCallback(async () => {
    if (!hasData || !selectedUseCase) return;
    
    try {
      const params = { 
        use_case: selectedUseCase,
        period: selectedPeriod
      };
      const response = await api.get('/api/kpi/analysis', { params });
      setAnalysisData(response.data.analysis || []);
      
      if (response.data.analysis?.length > 0) {
        setLaunchDate(response.data.analysis[0].launch_date);
        setTotalPostDays(response.data.analysis[0].total_post_days || 0);
        setActualPeriodDays(response.data.analysis[0].period_days || 0);
      }
    } catch (err) {
      console.error('Error fetching analysis:', err);
    }
  }, [hasData, selectedUseCase, selectedPeriod]);

  const fetchDailyData = useCallback(async () => {
    if (!hasData || !selectedUseCase) return;
    
    try {
      const params = { 
        kpi: selectedKPI,
        use_case: selectedUseCase,
        period: selectedPeriod
      };
      const response = await api.get('/api/kpi/daily', { params });
      setDailyData(response.data.data || []);
    } catch (err) {
      console.error('Error fetching daily data:', err);
    }
  }, [hasData, selectedUseCase, selectedKPI, selectedPeriod]);

  useEffect(() => {
    fetchDataStatus();
  }, [fetchDataStatus]);

  useEffect(() => {
    if (hasData && selectedUseCase) {
      fetchAnalysisData();
      fetchDailyData();
    }
  }, [hasData, selectedUseCase, selectedPeriod, fetchAnalysisData, fetchDailyData]);

  useEffect(() => {
    if (hasData && selectedUseCase) {
      fetchDailyData();
    }
  }, [selectedKPI, fetchDailyData, hasData, selectedUseCase]);

  const getMetricData = (kpi) => {
    const metric = analysisData.find(d => d.kpi === kpi.toUpperCase());
    if (!metric) {
      return { 
        postLift: 0, 
        postTY: 0, 
        postLY: 0,
        preLift: 0,
        preTY: 0,
        preLY: 0,
        compLift: 0
      };
    }
    return {
      postLift: metric.post_lift,
      postTY: metric.post_ty,
      postLY: metric.post_ly,
      preLift: metric.pre_lift,
      preTY: metric.pre_ty,
      preLY: metric.pre_ly,
      compLift: metric.pre_post_comp_lift
    };
  };

  const getPeriodLabel = () => {
    if (!launchDate) return '';
    
    if (selectedPeriod === 'all') {
      return `${totalPostDays}d post-launch`;
    }
    
    const periodDays = parseInt(selectedPeriod);
    
    if (actualPeriodDays < periodDays) {
      return `${actualPeriodDays}d of ${periodDays}d`;
    }
    
    return `${periodDays}d post-launch`;
  };

  const getCardPeriodLabel = () => {
    return `${actualPeriodDays} days`;
  };

  const handleDataUploaded = () => {
    setHasData(true);
    fetchDataStatus();
    setActiveView('overview'); // Switch back to overview after upload
  };

  const handleUseCaseChange = (useCase) => {
    setSelectedUseCase(useCase);
    
    const config = useCaseConfigs.find(c => c.use_case === useCase);
    if (config) {
      setLaunchDate(config.launch_date);
    }
  };

  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
  };

  const handleKPISelect = (kpi) => {
    setSelectedKPI(kpi);
  };

  const handleExpandMetric = (data) => {
    setModalData(data);
  };

  const handleCloseModal = () => {
    setModalData(null);
  };

  const handleOpenSendModal = () => {
    setShowSendModal(true);
  };

  const handleCloseSendModal = () => {
    setShowSendModal(false);
  };

  // Render content based on activeView
  const renderContent = () => {
    // IMPORTANT: Check for 'upload' view FIRST before checking hasData
    if (activeView === 'upload') {
      return <DataUpload onDataUploaded={handleDataUploaded} />;
    }

    // Show empty state if no data (and not on upload view)
    if (!hasData) {
      return (
        <div className="empty-state glass-heavy">
          <div className="empty-icon">
            <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
              <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
              <polyline points="14 2 14 8 20 8"></polyline>
              <line x1="12" y1="18" x2="12" y2="12"></line>
              <line x1="9" y1="15" x2="15" y2="15"></line>
            </svg>
          </div>
          <h2>No Data Available</h2>
          <p>Upload data using Developer Mode to view analytics</p>
        </div>
      );
    }

    // Show Analysis view
    if (activeView === 'analysis') {
      return (
        <AdvancedAnalysis 
          analysisData={analysisData} 
          useCases={useCases}
          selectedUseCase={selectedUseCase}
          onUseCaseChange={handleUseCaseChange}
          selectedPeriod={selectedPeriod}
          periodLabel={getPeriodLabel()}
          launchDate={launchDate}
        />
      );
    }

    // Default: Overview
    return (
      <>
        {/* Metrics Section */}
        <section className="metrics-section">
          <div className="section-header">
            <h2>Post-Launch Performance</h2>
            {launchDate && (
              <span className="period-badge">{getPeriodLabel()}</span>
            )}
          </div>
          
          <div className="metrics-container">
            <div className="metrics-grid">
              {Object.keys(kpiConfig).map(kpi => {
                const data = getMetricData(kpi);
                return (
                  <MetricCard
                    key={kpi}
                    kpiKey={kpi}
                    label={kpiConfig[kpi].shortLabel}
                    postLift={data.postLift}
                    postTY={data.postTY}
                    postLY={data.postLY}
                    preLift={data.preLift}
                    preTY={data.preTY}
                    preLY={data.preLY}
                    compLift={data.compLift}
                    format={kpiConfig[kpi].format}
                    periodLabel={getCardPeriodLabel()}
                    isSelected={selectedKPI === kpi}
                    onSelect={handleKPISelect}
                    onExpand={handleExpandMetric}
                  />
                );
              })}
            </div>
          </div>
        </section>

        {/* Chart Section */}
        <section className="chart-section">
          <div className="chart-header">
            <div className="chart-title">
              <h2>{kpiConfig[selectedKPI].label}</h2>
            </div>
            <div className="chart-controls">
              <div className="chart-type-selector">
                <button 
                  className={`chart-type-btn ${chartType === 'line' ? 'active' : ''}`}
                  onClick={() => setChartType('line')}
                  title="Line"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <polyline points="22 12 18 12 15 21 9 3 6 12 2 12"></polyline>
                  </svg>
                </button>
                <button 
                  className={`chart-type-btn ${chartType === 'area' ? 'active' : ''}`}
                  onClick={() => setChartType('area')}
                  title="Area"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M3 3v18h18"></path>
                    <path d="M3 18l6-6 4 4 8-8v10H3z" fill="currentColor" opacity="0.2"></path>
                  </svg>
                </button>
                <button 
                  className={`chart-type-btn ${chartType === 'bar' ? 'active' : ''}`}
                  onClick={() => setChartType('bar')}
                  title="Bar"
                >
                  <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <rect x="4" y="12" width="4" height="8"></rect>
                    <rect x="10" y="8" width="4" height="12"></rect>
                    <rect x="16" y="4" width="4" height="16"></rect>
                  </svg>
                </button>
              </div>
            </div>
          </div>
          
          <div className="chart-container">
            <KPIChart
              data={dailyData}
              kpi={selectedKPI}
              chartType={chartType}
              format={kpiConfig[selectedKPI].format}
              launchDate={launchDate}
            />
          </div>
        </section>
      </>
    );
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading dashboard</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <Header 
        activeView={activeView}
        setActiveView={setActiveView}
        useCases={useCases}
        selectedUseCase={selectedUseCase}
        onUseCaseChange={handleUseCaseChange}
        selectedPeriod={selectedPeriod}
        onPeriodChange={handlePeriodChange}
        periodOptions={periodOptions}
        lastUpdated={lastUpdated}
        hasData={hasData}
        onSendReport={handleOpenSendModal}
      />

      <main className="dashboard-content">
        {renderContent()}
      </main>

      {modalData && (
        <MetricModal data={modalData} onClose={handleCloseModal} />
      )}

      {showSendModal && (
        <SendReportModal
          useCase={selectedUseCase}
          period={selectedPeriod}
          periodLabel={getPeriodLabel()}
          onClose={handleCloseSendModal}
        />
      )}
    </div>
  );
}

export default Dashboard;
