import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import DonutChart from './DonutChart';
import KPIChart from './KPIChart';
import AdvancedAnalysis from './AdvancedAnalysis';
import DataUpload from './DataUpload';
import api from '../utils/api';
import './Dashboard.css';

function Dashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [kpiSummary, setKpiSummary] = useState(null);
  const [dailyData, setDailyData] = useState([]);
  const [useCases, setUseCases] = useState([]);
  const [selectedUseCase, setSelectedUseCase] = useState('');
  const [selectedKPI, setSelectedKPI] = useState('visits');
  const [chartType, setChartType] = useState('line');
  const [loading, setLoading] = useState(true);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [hasData, setHasData] = useState(false);
  const [error, setError] = useState(null);

  const kpiConfig = {
    visits: { label: 'Visits', color: '#F96302', target: 1000000, format: 'number' },
    orders: { label: 'Orders', color: '#FF8C42', target: 50000, format: 'number' },
    revenue: { label: 'Revenue', color: '#FFB347', target: 5000000, format: 'currency' },
    cvr: { label: 'CVR', color: '#E55A02', target: 5, format: 'percentage' },
    aov: { label: 'AOV', color: '#CC4E02', target: 150, format: 'currency' },
    rpv: { label: 'RPV', color: '#B34502', target: 10, format: 'currency' }
  };

  useEffect(() => {
    fetchDataStatus();
  }, []);

  useEffect(() => {
    if (hasData) {
      fetchKPISummary();
      fetchDailyData();
      fetchUseCases();
    }
  }, [hasData, selectedUseCase, selectedKPI]);

  const fetchDataStatus = async () => {
    try {
      const response = await api.get('/api/data/status');
      setHasData(response.data.has_data);
      setLastUpdated(response.data.last_updated);
      if (response.data.use_cases && response.data.use_cases.length > 0 && !selectedUseCase) {
        setSelectedUseCase(response.data.use_cases[0]);
      }
      setError(null);
    } catch (err) {
      console.error('Error fetching data status:', err);
      setError('Failed to connect to server');
    } finally {
      setLoading(false);
    }
  };

  const fetchKPISummary = async () => {
    try {
      const params = selectedUseCase ? { use_case: selectedUseCase } : {};
      const response = await api.get('/api/kpi/summary', { params });
      console.log('KPI Summary Response:', response.data);
      setKpiSummary(response.data.summary);
      setLastUpdated(response.data.last_updated);
      setError(null);
    } catch (err) {
      console.error('Error fetching KPI summary:', err);
      setError('Failed to fetch KPI summary');
    }
  };

  const fetchDailyData = async () => {
    try {
      const params = { kpi: selectedKPI };
      if (selectedUseCase) params.use_case = selectedUseCase;
      const response = await api.get('/api/kpi/daily', { params });
      console.log('Daily Data Response:', response.data);
      setDailyData(response.data.data || []);
      setError(null);
    } catch (err) {
      console.error('Error fetching daily data:', err);
      setError('Failed to fetch daily data');
    }
  };

  const fetchUseCases = async () => {
    try {
      const response = await api.get('/api/use-cases');
      setUseCases(response.data.use_cases || []);
      setError(null);
    } catch (err) {
      console.error('Error fetching use cases:', err);
    }
  };

  const handleDataUploaded = () => {
    setHasData(true);
    fetchDataStatus();
  };

  const renderDonutCharts = () => {
    if (!kpiSummary) {
      return (
        <div className="donut-grid">
          {Object.keys(kpiConfig).map(kpi => (
            <div key={kpi} className="donut-chart-container glass-card">
              <h4 className="donut-title">{kpiConfig[kpi].label}</h4>
              <div className="donut-loading">Loading...</div>
            </div>
          ))}
        </div>
      );
    }

    return (
      <div className="donut-grid">
        {Object.keys(kpiConfig).map(kpi => (
          <DonutChart
            key={kpi}
            title={kpiConfig[kpi].label}
            value={kpiSummary[kpi]}
            target={kpiConfig[kpi].target}
            color={kpiConfig[kpi].color}
            format={kpiConfig[kpi].format}
          />
        ))}
      </div>
    );
  };

  const renderContent = () => {
    if (loading) {
      return (
        <div className="loading-state">
          <div className="loading-spinner"></div>
          <p>Loading dashboard...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div className="error-state glass-card-solid">
          <div className="error-icon">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
            </svg>
          </div>
          <h3>Error</h3>
          <p>{error}</p>
          <button className="retry-button" onClick={fetchDataStatus}>Retry</button>
        </div>
      );
    }

    if (!hasData && activeTab !== 'upload') {
      return (
        <div className="no-data-state glass-card-solid">
          <div className="no-data-icon">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M9 17v-2m3 2v-4m3 4v-6m2 10H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
          </div>
          <h3>No Data Available</h3>
          <p>Please upload data to view the dashboard. Contact a developer to upload data.</p>
        </div>
      );
    }

    switch (activeTab) {
      case 'analysis':
        return <AdvancedAnalysis useCases={useCases} />;
      case 'upload':
        return <DataUpload onDataUploaded={handleDataUploaded} />;
      default:
        return (
          <>
            <div className="dashboard-header">
              <div className="header-left">
                <h1>KPI Overview</h1>
                {lastUpdated && (
                  <span className="last-updated">
                    Last updated: {new Date(lastUpdated).toLocaleString()}
                  </span>
                )}
              </div>
              <div className="header-controls">
                <select 
                  value={selectedUseCase} 
                  onChange={(e) => setSelectedUseCase(e.target.value)}
                  className="select-control"
                >
                  <option value="">All Use Cases</option>
                  {useCases.map(uc => (
                    <option key={uc} value={uc}>{uc}</option>
                  ))}
                </select>
              </div>
            </div>

            {renderDonutCharts()}

            <div className="chart-section">
              <div className="chart-controls">
                <div className="control-group">
                  <label>KPI</label>
                  <select 
                    value={selectedKPI} 
                    onChange={(e) => setSelectedKPI(e.target.value)}
                    className="select-control"
                  >
                    {Object.keys(kpiConfig).map(kpi => (
                      <option key={kpi} value={kpi}>{kpiConfig[kpi].label}</option>
                    ))}
                  </select>
                </div>
                <div className="control-group">
                  <label>Chart Type</label>
                  <div className="chart-type-buttons">
                    {['line', 'area', 'bar'].map(type => (
                      <button
                        key={type}
                        className={`type-button ${chartType === type ? 'active' : ''}`}
                        onClick={() => setChartType(type)}
                      >
                        {type.charAt(0).toUpperCase() + type.slice(1)}
                      </button>
                    ))}
                  </div>
                </div>
              </div>
              
              {dailyData.length > 0 ? (
                <KPIChart
                  data={dailyData}
                  kpi={selectedKPI}
                  chartType={chartType}
                  title={`Daily ${kpiConfig[selectedKPI].label}`}
                />
              ) : (
                <div className="no-chart-data glass-card-solid">
                  <p>No data available for the selected filters</p>
                </div>
              )}
            </div>
          </>
        );
    }
  };

  return (
    <div className="dashboard-layout">
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />
      <main className="dashboard-main">
        {renderContent()}
      </main>
    </div>
  );
}

export default Dashboard;
