import React, { useState } from 'react';
import './MetricCard.css';

function MetricCard({ 
  label, 
  kpiKey,
  postLift, 
  postTY, 
  postLY, 
  preLift,
  preTY,
  preLY,
  compLift,
  format,
  periodLabel,
  isSelected,
  onSelect,
  onExpand
}) {
  const [isHovered, setIsHovered] = useState(false);
  
  const isPositive = postLift > 0;
  const isNegative = postLift < 0;

  const formatValue = (value, compact = false) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    
    if (format === 'currency') {
      if (Math.abs(value) >= 1000000) {
        return `$${(value / 1000000).toFixed(compact ? 1 : 2)}M`;
      }
      if (Math.abs(value) >= 1000) {
        return `$${(value / 1000).toFixed(compact ? 0 : 1)}K`;
      }
      return `$${value.toFixed(0)}`;
    }
    if (format === 'percentage') {
      return `${value.toFixed(2)}%`;
    }
    if (Math.abs(value) >= 1000000) {
      return `${(value / 1000000).toFixed(compact ? 1 : 2)}M`;
    }
    if (Math.abs(value) >= 1000) {
      return `${(value / 1000).toFixed(compact ? 0 : 1)}K`;
    }
    return Math.round(value).toLocaleString();
  };

  const formatLift = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    return `${Math.abs(value).toFixed(1)}%`;
  };

  const formatChange = () => {
    if (postTY === null || postLY === null || isNaN(postTY) || isNaN(postLY)) return '-';
    
    const change = postTY - postLY;
    
    if (format === 'currency') {
      if (Math.abs(change) >= 1000000) {
        return `${change >= 0 ? '+' : '-'}$${Math.abs(change / 1000000).toFixed(1)}M`;
      }
      if (Math.abs(change) >= 1000) {
        return `${change >= 0 ? '+' : '-'}$${Math.abs(change / 1000).toFixed(0)}K`;
      }
      return `${change >= 0 ? '+' : '-'}$${Math.abs(change).toFixed(0)}`;
    }
    if (format === 'percentage') {
      return `${change >= 0 ? '+' : ''}${change.toFixed(2)}pp`;
    }
    if (Math.abs(change) >= 1000000) {
      return `${change >= 0 ? '+' : ''}${(change / 1000000).toFixed(1)}M`;
    }
    if (Math.abs(change) >= 1000) {
      return `${change >= 0 ? '+' : ''}${(change / 1000).toFixed(0)}K`;
    }
    return `${change >= 0 ? '+' : ''}${Math.round(change).toLocaleString()}`;
  };

  const getArrow = () => {
    if (isPositive) return '↑';
    if (isNegative) return '↓';
    return '–';
  };

  // Calculate stroke dasharray for the progress ring
  const radius = 85;
  const circumference = 2 * Math.PI * radius;
  const maxLift = 50; // Cap at 50% for visual purposes
  const normalizedLift = Math.min(Math.abs(postLift || 0), maxLift);
  const progress = (normalizedLift / maxLift) * circumference;

  const handleCardClick = () => {
    if (onSelect) {
      onSelect(kpiKey);
    }
  };

  const handleExpandClick = (e) => {
    e.stopPropagation();
    if (onExpand) {
      onExpand({
        label,
        kpiKey,
        postLift,
        postTY,
        postLY,
        preLift,
        preTY,
        preLY,
        compLift,
        format
      });
    }
  };

  return (
    <div 
      className={`metric-card 
        ${isPositive ? 'positive' : ''} 
        ${isNegative ? 'negative' : ''}
        ${isSelected ? 'selected' : ''}
        ${isHovered ? 'hovered' : ''}`}
      onClick={handleCardClick}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Progress Ring */}
      <svg className="progress-ring" viewBox="0 0 200 200">
        {/* Background ring */}
        <circle
          className="progress-ring-bg"
          cx="100"
          cy="100"
          r={radius}
          fill="none"
          strokeWidth="6"
        />
        {/* Progress ring */}
        <circle
          className="progress-ring-progress"
          cx="100"
          cy="100"
          r={radius}
          fill="none"
          strokeWidth="6"
          strokeDasharray={circumference}
          strokeDashoffset={circumference - progress}
          strokeLinecap="round"
          transform="rotate(-90 100 100)"
        />
      </svg>

      {/* Card Content */}
      <div className="card-content">
        <span className="card-label">{label}</span>
        
        <div className="metric-display">
          <span className={`metric-arrow ${isPositive ? 'up' : ''} ${isNegative ? 'down' : ''}`}>
            {getArrow()}
          </span>
          <span className="metric-value">{formatLift(postLift)}</span>
        </div>

        <div className={`metric-change ${isPositive ? 'positive' : ''} ${isNegative ? 'negative' : ''}`}>
          {formatChange()}
        </div>

        {/* Hover Details */}
        <div className="hover-details">
          <div className="detail-row">
            <span className="detail-label">TY</span>
            <span className="detail-value">{formatValue(postTY)}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">LY</span>
            <span className="detail-value">{formatValue(postLY)}</span>
          </div>
          {periodLabel && (
            <div className="period-label">{periodLabel}</div>
          )}
        </div>
      </div>

      {/* Info Button */}
      <button 
        className="info-btn"
        onClick={handleExpandClick}
        aria-label="View details"
      >
        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
          <circle cx="12" cy="12" r="10"></circle>
          <line x1="12" y1="16" x2="12" y2="12"></line>
          <line x1="12" y1="8" x2="12.01" y2="8"></line>
        </svg>
      </button>
    </div>
  );
}

export default MetricCard;
