import React, { useState, useEffect, useRef } from 'react';
import api from '../utils/api';
import './FeedbackButton.css';

const FEEDBACK_TOPICS = [
  { id: 'metrics', label: 'Metric Cards', description: 'KPI values, lift calculations, formatting' },
  { id: 'chart', label: 'Chart & Trends', description: 'Daily chart, event tags, display modes' },
  { id: 'filters', label: 'Filters & Controls', description: 'Period, segments, device, page type' },
  { id: 'analysis', label: 'Advanced Analysis', description: 'Pre/post comparison table' },
  { id: 'reports', label: 'Reports & Email', description: 'PDF reports, email sending' },
  { id: 'general', label: 'General', description: 'Overall experience, bugs, suggestions' },
];

// Mini visual previews for each topic
function TopicPreview({ topicId }) {
  switch (topicId) {
    case 'metrics':
      return (
        <div className="topic-preview">
          <div className="preview-label">Metric Cards</div>
          <div className="preview-metrics">
            <div className="preview-metric-card">
              <div className="preview-ring green" />
              <div className="preview-metric-info">
                <span className="preview-metric-name">Visits</span>
                <span className="preview-metric-lift green">+3.2%</span>
              </div>
            </div>
            <div className="preview-metric-card">
              <div className="preview-ring red" />
              <div className="preview-metric-info">
                <span className="preview-metric-name">Orders</span>
                <span className="preview-metric-lift red">-1.1%</span>
              </div>
            </div>
            <div className="preview-metric-card">
              <div className="preview-ring green" />
              <div className="preview-metric-info">
                <span className="preview-metric-name">Revenue</span>
                <span className="preview-metric-lift green">+5.7%</span>
              </div>
            </div>
          </div>
          <div className="preview-metrics">
            <div className="preview-metric-card">
              <div className="preview-ring neutral" />
              <div className="preview-metric-info">
                <span className="preview-metric-name">CVR</span>
                <span className="preview-metric-lift neutral">0 bps</span>
              </div>
            </div>
            <div className="preview-metric-card">
              <div className="preview-ring green" />
              <div className="preview-metric-info">
                <span className="preview-metric-name">AOV</span>
                <span className="preview-metric-lift green">+2.4%</span>
              </div>
            </div>
            <div className="preview-metric-card">
              <div className="preview-ring green" />
              <div className="preview-metric-info">
                <span className="preview-metric-name">RPV</span>
