import React from 'react';
import {
  LineChart, Line, AreaChart, Area, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend
} from 'recharts';

function KPIChart({ data, kpi, chartType, title }) {
    const formatValue = (val) => {
    if (val === null || val === undefined || isNaN(val)) {
        return '-';
    }
    
    if (kpi === 'revenue') {
        return new Intl.NumberFormat('en-US', { 
        style: 'currency', 
        currency: 'USD',
        notation: 'compact'
        }).format(val);
    }
    if (kpi === 'cvr') {
        return `${val.toFixed(4)}%`;
    }
    if (kpi === 'rpv') {
        return new Intl.NumberFormat('en-US', { 
        style: 'currency', 
        currency: 'USD',
        minimumFractionDigits: 2,
        maximumFractionDigits: 4
        }).format(val);
    }
    if (kpi === 'aov') {
        return new Intl.NumberFormat('en-US', { 
        style: 'currency', 
        currency: 'USD',
        minimumFractionDigits: 2,
        maximumFractionDigits: 2
        }).format(val);
    }
    return new Intl.NumberFormat('en-US', { notation: 'compact' }).format(val);
    };

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div style={{
          background: 'rgba(255,255,255,0.95)',
          padding: '12px 16px',
          borderRadius: '12px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
          border: 'none'
        }}>
          <p style={{ margin: 0, fontWeight: 500, marginBottom: 4 }}>{label}</p>
          <p style={{ margin: 0, color: '#F96302', fontWeight: 600 }}>
            {formatValue(payload[0].value)}
          </p>
        </div>
      );
    }
    return null;
  };

  const renderChart = () => {
    const commonProps = {
      data: data,
      margin: { top: 20, right: 30, left: 20, bottom: 20 }
    };

    switch (chartType) {
      case 'area':
        return (
          <AreaChart {...commonProps}>
            <defs>
              <linearGradient id="colorKpi" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#F96302" stopOpacity={0.3}/>
                <stop offset="95%" stopColor="#F96302" stopOpacity={0}/>
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.05)" />
            <XAxis 
              dataKey="date" 
              tick={{ fontSize: 11, fill: '#86868b' }}
              tickLine={false}
              axisLine={{ stroke: 'rgba(0,0,0,0.1)' }}
            />
            <YAxis 
              tick={{ fontSize: 11, fill: '#86868b' }}
              tickLine={false}
              axisLine={false}
              tickFormatter={formatValue}
            />
            <Tooltip content={<CustomTooltip />} />
            <Area 
              type="monotone" 
              dataKey={kpi} 
              stroke="#F96302" 
              strokeWidth={2}
              fillOpacity={1} 
              fill="url(#colorKpi)" 
            />
          </AreaChart>
        );
      case 'bar':
        return (
          <BarChart {...commonProps}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.05)" />
            <XAxis 
              dataKey="date" 
              tick={{ fontSize: 11, fill: '#86868b' }}
              tickLine={false}
              axisLine={{ stroke: 'rgba(0,0,0,0.1)' }}
            />
            <YAxis 
              tick={{ fontSize: 11, fill: '#86868b' }}
              tickLine={false}
              axisLine={false}
              tickFormatter={formatValue}
            />
            <Tooltip content={<CustomTooltip />} />
            <Bar 
              dataKey={kpi} 
              fill="#F96302" 
              radius={[4, 4, 0, 0]}
            />
          </BarChart>
        );
      default:
        return (
          <LineChart {...commonProps}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.05)" />
            <XAxis 
              dataKey="date" 
              tick={{ fontSize: 11, fill: '#86868b' }}
              tickLine={false}
              axisLine={{ stroke: 'rgba(0,0,0,0.1)' }}
            />
            <YAxis 
              tick={{ fontSize: 11, fill: '#86868b' }}
              tickLine={false}
              axisLine={false}
              tickFormatter={formatValue}
            />
            <Tooltip content={<CustomTooltip />} />
            <Line 
              type="monotone" 
              dataKey={kpi} 
              stroke="#F96302" 
              strokeWidth={2}
              dot={{ fill: '#F96302', strokeWidth: 0, r: 3 }}
              activeDot={{ r: 5, fill: '#F96302' }}
            />
          </LineChart>
        );
    }
  };

  return (
    <div className="kpi-chart glass-card-solid">
      <h3 className="chart-title">{title}</h3>
      <ResponsiveContainer width="100%" height={300}>
        {renderChart()}
      </ResponsiveContainer>
    </div>
  );
}

export default KPIChart;
