import { useState, useRef, useCallback, useEffect } from 'react';
import {
  LineChart, Line, AreaChart, Area, BarChart, Bar,
  XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer,
  ReferenceLine, Legend
} from 'recharts';
import { useAuth } from '../context/AuthContext';
import './KPIChart.css';

// Preset event tags
const PRESET_TAGS = [
  { id: 'thanksgiving', name: 'Thanksgiving', color: '#F96302' },
  { id: 'blackfriday', name: 'Black Friday', color: '#1d1d1f' },
  { id: 'laborday', name: 'Labor Day', color: '#007AFF' },
  { id: 'july4th', name: '4th of July', color: '#FF3B30' },
  { id: 'tier1', name: 'Tier 1 Event', color: '#5856D6' }
];

// Chart margins matching Recharts config
const CHART_MARGINS = { top: 20, right: 20, left: 10, bottom: 20 };
const Y_AXIS_WIDTH = 60;
const CHART_HEIGHT = 320;

function KPIChart({ data, kpi, chartType, format, launchDate, comparisonData, chartTags, onTagsChange }) {
  const { isBetaMode } = useAuth();
  const isBeta = isBetaMode();
  const chartContainerRef = useRef(null);
  const [showTagPanel, setShowTagPanel] = useState(false);
  const [customTagName, setCustomTagName] = useState('');
  const [customTagOwner, setCustomTagOwner] = useState('');
  const [draggedTag, setDraggedTag] = useState(null);
  const [showCustomForm, setShowCustomForm] = useState(false);
  const [dragPreview, setDragPreview] = useState(null);

  // Use comparison data if available, otherwise fall back to regular data
  const chartData = comparisonData && comparisonData.length > 0 ? comparisonData : data;
  const hasComparison = comparisonData && comparisonData.length > 0;

  const formatValue = (val) => {
    if (val === null || val === undefined || isNaN(val)) return '-';

    if (format === 'currency') {
      if (Math.abs(val) >= 1000000) {
        return `$${(val / 1000000).toFixed(1)}M`;
      }
      if (Math.abs(val) >= 1000) {
        return `$${(val / 1000).toFixed(0)}K`;
      }
      return `$${val.toFixed(2)}`;
    }
    if (format === 'percentage') {
      return `${(val * 100).toFixed(2)}%`;
    }
    if (Math.abs(val) >= 1000000) {
      return `${(val / 1000000).toFixed(1)}M`;
    }
    if (Math.abs(val) >= 1000) {
      return `${(val / 1000).toFixed(0)}K`;
    }
    return val.toLocaleString();
  };

  const formatAxisDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
  };

  const formatFullDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  // Calculate date index from x position
  const getDateIndexFromX = useCallback((clientX) => {
    if (!chartContainerRef.current || !chartData || chartData.length === 0) return -1;

    const rect = chartContainerRef.current.getBoundingClientRect();
    const chartAreaLeft = Y_AXIS_WIDTH + CHART_MARGINS.left;
    const chartAreaWidth = rect.width - chartAreaLeft - CHART_MARGINS.right;

    const relativeX = clientX - rect.left - chartAreaLeft;
    const percentage = relativeX / chartAreaWidth;

    const dataIndex = Math.round(percentage * (chartData.length - 1));
    return Math.max(0, Math.min(dataIndex, chartData.length - 1));
  }, [chartData]);

  // Get x position for a data index
  const getXPositionForIndex = useCallback((index) => {
    if (!chartDimensions.width || !chartData || chartData.length === 0) return 0;

    const chartAreaLeft = Y_AXIS_WIDTH + CHART_MARGINS.left;
    const chartAreaWidth = chartDimensions.width - chartAreaLeft - CHART_MARGINS.right;

    const percentage = index / (chartData.length - 1);
    return chartAreaLeft + (percentage * chartAreaWidth);
  }, [chartDimensions.width, chartData]);

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      const dateStr = new Date(label).toLocaleDateString('en-US', {
        weekday: 'short',
        month: 'short',
        day: 'numeric',
        year: 'numeric'
      });

      // Check if there's a tag for this date
      const dateTag = chartTags?.find(t => t.date === label);

      return (
        <div className="chart-tooltip">
          <p className="tooltip-date">{dateStr}</p>
          {dateTag && (
            <p className="tooltip-tag">
              <span className="tooltip-tag-marker" style={{ background: dateTag.color }}></span>
              {dateTag.name}
            </p>
          )}
          {payload.map((entry, index) => (
            <p key={index} className="tooltip-value" style={{ color: entry.color }}>
              <span className="tooltip-label">{entry.name}: </span>
              {formatValue(entry.value)}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  const handleDragStart = (e, tag) => {
    setDraggedTag(tag);
    e.dataTransfer.effectAllowed = 'copy';
    // Set a transparent drag image
    const img = new Image();
    img.src = 'data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7';
    e.dataTransfer.setDragImage(img, 0, 0);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'copy';

    if (!draggedTag || !chartData || chartData.length === 0) return;

    const dateIndex = getDateIndexFromX(e.clientX);
    const targetDate = chartData[dateIndex]?.date;
    const xPosition = getXPositionForIndex(dateIndex);

    if (targetDate && dateIndex >= 0) {
      setDragPreview({
        date: targetDate,
        formattedDate: formatFullDate(targetDate),
        x: xPosition,
        index: dateIndex,
        tag: draggedTag
      });
    }
  };

  const handleDragLeave = (e) => {
    // Only clear preview if we're actually leaving the chart area
    const rect = chartContainerRef.current?.getBoundingClientRect();
    if (rect) {
      const { clientX, clientY } = e;
      if (clientX < rect.left || clientX > rect.right ||
          clientY < rect.top || clientY > rect.bottom) {
        setDragPreview(null);
      }
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    if (!draggedTag || !chartData || chartData.length === 0) return;

    const dateIndex = getDateIndexFromX(e.clientX);
    const targetDate = chartData[dateIndex]?.date;

    if (targetDate) {
      const newTag = {
        ...draggedTag,
        id: `${draggedTag.id}-${Date.now()}`,
        date: targetDate,
        dateIndex: dateIndex
      };

      if (onTagsChange) {
        onTagsChange([...(chartTags || []), newTag]);
      }
    }

    setDraggedTag(null);
    setDragPreview(null);
  };

  const handleDragEnd = () => {
    setDragPreview(null);
  };

  const handleAddCustomTag = () => {
    if (!customTagName.trim() || !customTagOwner.trim()) return;

    const newTag = {
      id: `custom-${Date.now()}`,
      name: customTagName.trim(),
      owner: customTagOwner.trim(),
      color: '#6e6e73',
      isCustom: true
    };

    setDraggedTag(newTag);
    setCustomTagName('');
    setCustomTagOwner('');
    setShowCustomForm(false);
  };

  const handleRemoveTag = (tagId) => {
    if (onTagsChange) {
      onTagsChange((chartTags || []).filter(t => t.id !== tagId));
    }
  };

  const commonProps = {
    data: chartData,
    margin: CHART_MARGINS
  };

  const axisStyle = {
    fontSize: 11,
    fill: '#86868b',
    fontWeight: 500
  };

  // Get the correct data keys based on comparison mode
  const tyDataKey = hasComparison ? `${kpi}_ty` : kpi;
  const lyDataKey = hasComparison ? `${kpi}_ly` : null;

  // Render placed tags as overlay elements
  const renderPlacedTags = () => {
    if (!chartTags || chartTags.length === 0 || !chartDimensions.width) return null;

    return chartTags.map((tag) => {
      const dateIndex = chartData?.findIndex(d => d.date === tag.date);
      if (dateIndex === -1) return null;

      const xPos = getXPositionForIndex(dateIndex);
      const chartHeight = 320; // Fixed chart height

      return (
        <div
          key={tag.id}
          className="placed-tag-container"
          style={{ left: xPos }}
        >
          {/* Dotted line from tag to axis */}
          <div
            className="tag-line"
            style={{
              borderColor: tag.color,
              height: chartHeight - CHART_MARGINS.top - CHART_MARGINS.bottom - 30
            }}
          />
          {/* Glass tag badge */}
          <div
            className="placed-tag"
            style={{
              '--tag-color': tag.color,
              borderColor: tag.color
            }}
          >
            <span className="placed-tag-marker" style={{ background: tag.color }}></span>
            <span className="placed-tag-name">{tag.name}</span>
            {tag.owner && <span className="placed-tag-owner">by {tag.owner}</span>}
            <button
              className="placed-tag-remove"
              onClick={() => handleRemoveTag(tag.id)}
              title="Remove tag"
            >
              <svg width="10" height="10" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="18" y1="6" x2="6" y2="18"></line>
                <line x1="6" y1="6" x2="18" y2="18"></line>
              </svg>
            </button>
          </div>
          {/* Date label at bottom */}
          <div className="tag-date-label" style={{ color: tag.color }}>
            {formatAxisDate(tag.date)}
          </div>
        </div>
      );
    });
  };

  // Render drag preview
  const renderDragPreview = () => {
    if (!dragPreview) return null;

    return (
      <div
        className="drag-preview-container"
        style={{ left: dragPreview.x }}
      >
        {/* Preview dotted line */}
        <div
          className="preview-line"
          style={{ borderColor: dragPreview.tag.color }}
        />
        {/* Preview tag */}
        <div
          className="preview-tag"
          style={{
            '--tag-color': dragPreview.tag.color,
            borderColor: dragPreview.tag.color
          }}
        >
          <span className="preview-tag-marker" style={{ background: dragPreview.tag.color }}></span>
          <span className="preview-tag-name">{dragPreview.tag.name}</span>
        </div>
        {/* Date display */}
        <div className="preview-date" style={{ borderColor: dragPreview.tag.color }}>
          {dragPreview.formattedDate}
        </div>
      </div>
    );
  };

  const renderChart = () => {
    switch (chartType) {
      case 'area':
        return (
          <AreaChart {...commonProps}>
            <defs>
              <linearGradient id="areaGradientTY" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="#F96302" stopOpacity={0.25}/>
                <stop offset="100%" stopColor="#F96302" stopOpacity={0}/>
              </linearGradient>
              <linearGradient id="areaGradientLY" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="#86868b" stopOpacity={0.15}/>
                <stop offset="100%" stopColor="#86868b" stopOpacity={0}/>
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.04)" vertical={false} />
            <XAxis
              dataKey="date"
              tick={axisStyle}
              tickLine={false}
              axisLine={{ stroke: 'rgba(0,0,0,0.08)' }}
              tickFormatter={formatAxisDate}
              interval="preserveStartEnd"
            />
            <YAxis
              tick={axisStyle}
              tickLine={false}
              axisLine={false}
              tickFormatter={formatValue}
              width={Y_AXIS_WIDTH}
            />
            <Tooltip content={<CustomTooltip />} />
            {hasComparison && <Legend />}
            {launchDate && (
              <ReferenceLine
                x={launchDate}
                stroke="#F96302"
                strokeDasharray="4 4"
                strokeWidth={2}
                label={{
                  value: 'Launch',
                  position: 'top',
                  fill: '#F96302',
                  fontSize: 11,
                  fontWeight: 600
                }}
              />
            )}
            {hasComparison && lyDataKey && (
              <Area
                type="monotone"
                dataKey={lyDataKey}
                name="Last Year"
                stroke="#86868b"
                strokeWidth={1.5}
                strokeDasharray="4 4"
                fill="url(#areaGradientLY)"
                dot={false}
                activeDot={{ r: 4, fill: '#86868b', stroke: '#fff', strokeWidth: 2 }}
              />
            )}
            <Area
              type="monotone"
              dataKey={tyDataKey}
              name={hasComparison ? "This Year" : kpi}
              stroke="#F96302"
              strokeWidth={2}
              fill="url(#areaGradientTY)"
              dot={false}
              activeDot={{ r: 5, fill: '#F96302', stroke: '#fff', strokeWidth: 2 }}
            />
          </AreaChart>
        );

      case 'bar':
        return (
          <BarChart {...commonProps}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.04)" vertical={false} />
            <XAxis
              dataKey="date"
              tick={axisStyle}
              tickLine={false}
              axisLine={{ stroke: 'rgba(0,0,0,0.08)' }}
              tickFormatter={formatAxisDate}
              interval="preserveStartEnd"
            />
            <YAxis
              tick={axisStyle}
              tickLine={false}
              axisLine={false}
              tickFormatter={formatValue}
              width={Y_AXIS_WIDTH}
            />
            <Tooltip content={<CustomTooltip />} />
            {hasComparison && <Legend />}
            {launchDate && (
              <ReferenceLine
                x={launchDate}
                stroke="#F96302"
                strokeDasharray="4 4"
                strokeWidth={2}
              />
            )}
            {hasComparison && lyDataKey && (
              <Bar
                dataKey={lyDataKey}
                name="Last Year"
                fill="#d1d1d6"
                radius={[4, 4, 0, 0]}
                maxBarSize={20}
              />
            )}
            <Bar
              dataKey={tyDataKey}
              name={hasComparison ? "This Year" : kpi}
              fill="#F96302"
              radius={[4, 4, 0, 0]}
              maxBarSize={hasComparison ? 20 : 40}
            />
          </BarChart>
        );

      default:
        return (
          <LineChart {...commonProps}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(0,0,0,0.04)" vertical={false} />
            <XAxis
              dataKey="date"
              tick={axisStyle}
              tickLine={false}
              axisLine={{ stroke: 'rgba(0,0,0,0.08)' }}
              tickFormatter={formatAxisDate}
              interval="preserveStartEnd"
            />
            <YAxis
              tick={axisStyle}
              tickLine={false}
              axisLine={false}
              tickFormatter={formatValue}
              width={Y_AXIS_WIDTH}
            />
            <Tooltip content={<CustomTooltip />} />
            {hasComparison && <Legend />}
            {launchDate && (
              <ReferenceLine
                x={launchDate}
                stroke="#F96302"
                strokeDasharray="4 4"
                strokeWidth={2}
                label={{
                  value: 'Launch',
                  position: 'top',
                  fill: '#F96302',
                  fontSize: 11,
                  fontWeight: 600
                }}
              />
            )}
            {hasComparison && lyDataKey && (
              <Line
                type="monotone"
                dataKey={lyDataKey}
                name="Last Year"
                stroke="#86868b"
                strokeWidth={1.5}
                strokeDasharray="4 4"
                dot={false}
                activeDot={{ r: 4, fill: '#86868b', stroke: '#fff', strokeWidth: 2 }}
              />
            )}
            <Line
              type="monotone"
              dataKey={tyDataKey}
              name={hasComparison ? "This Year" : kpi}
              stroke="#F96302"
              strokeWidth={2}
              dot={false}
              activeDot={{ r: 5, fill: '#F96302', stroke: '#fff', strokeWidth: 2 }}
            />
          </LineChart>
        );
    }
  };

  if (!chartData || chartData.length === 0) {
    return (
      <div className="chart-empty">
        <p>No data available for the selected period</p>
      </div>
    );
  }

  if (isBeta) {
    return (
      <div className="kpi-chart beta-restricted">
        <div className="chart-blur-wrapper">
          <ResponsiveContainer width="100%" height={320}>
            {renderChart()}
          </ResponsiveContainer>
        </div>
        <div className="beta-overlay">
          <div className="beta-overlay-content">
            <h3>Place holder</h3>
            <p>Chart visualization</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="kpi-chart-wrapper">
      <div
        className={`kpi-chart ${draggedTag ? 'drop-active' : ''}`}
        ref={chartContainerRef}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <ResponsiveContainer width="100%" height={320}>
          {renderChart()}
        </ResponsiveContainer>

        {/* Placed tags overlay */}
        <div className="tags-overlay">
          {renderPlacedTags()}
        </div>

        {/* Drag preview overlay */}
        {dragPreview && (
          <div className="drag-preview-overlay">
            {renderDragPreview()}
          </div>
        )}
      </div>

      {/* Tag Panel Toggle */}
      <div className="chart-tag-controls">
        <button
          className={`tag-panel-toggle ${showTagPanel ? 'active' : ''}`}
          onClick={() => setShowTagPanel(!showTagPanel)}
        >
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M20.59 13.41l-7.17 7.17a2 2 0 0 1-2.83 0L2 12V2h10l8.59 8.59a2 2 0 0 1 0 2.82z"></path>
            <line x1="7" y1="7" x2="7.01" y2="7"></line>
          </svg>
          <span>Add Info Tag</span>
        </button>

        {/* Active Tags Display */}
        {chartTags && chartTags.length > 0 && (
          <div className="active-tags">
            {chartTags.map(tag => (
              <span
                key={tag.id}
                className="active-tag"
                style={{ borderColor: tag.color, color: tag.color }}
              >
                <span className="tag-marker" style={{ background: tag.color }}></span>
                {tag.name}
                <span className="tag-date">{formatAxisDate(tag.date)}</span>
                <button onClick={() => handleRemoveTag(tag.id)} className="tag-remove">
                  <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <line x1="18" y1="6" x2="6" y2="18"></line>
                    <line x1="6" y1="6" x2="18" y2="18"></line>
                  </svg>
                </button>
              </span>
            ))}
          </div>
        )}
      </div>

      {/* Tag Panel */}
      {showTagPanel && (
        <div className="tag-panel">
          <div className="tag-panel-header">
            <h4>Drag tags onto the chart</h4>
            <button className="tag-panel-close" onClick={() => setShowTagPanel(false)}>
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="18" y1="6" x2="6" y2="18"></line>
                <line x1="6" y1="6" x2="18" y2="18"></line>
              </svg>
            </button>
          </div>

          <div className="preset-tags">
            {PRESET_TAGS.map(tag => (
              <div
                key={tag.id}
                className="draggable-tag"
                draggable
                onDragStart={(e) => handleDragStart(e, tag)}
                onDragEnd={handleDragEnd}
                style={{ borderColor: tag.color }}
              >
                <span className="tag-marker" style={{ background: tag.color }}></span>
                {tag.name}
              </div>
            ))}
          </div>

          <div className="custom-tag-section">
            {!showCustomForm ? (
              <button
                className="add-custom-tag-btn"
                onClick={() => setShowCustomForm(true)}
              >
                <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <line x1="12" y1="5" x2="12" y2="19"></line>
                  <line x1="5" y1="12" x2="19" y2="12"></line>
                </svg>
                Create Custom Tag
              </button>
            ) : (
              <div className="custom-tag-form">
                <input
                  type="text"
                  placeholder="Tag name"
                  value={customTagName}
                  onChange={(e) => setCustomTagName(e.target.value)}
                  className="custom-tag-input"
                />
                <input
                  type="text"
                  placeholder="Your name (owner)"
                  value={customTagOwner}
                  onChange={(e) => setCustomTagOwner(e.target.value)}
                  className="custom-tag-input"
                />
                <div className="custom-tag-actions">
                  <button
                    className="custom-tag-cancel"
                    onClick={() => {
                      setShowCustomForm(false);
                      setCustomTagName('');
                      setCustomTagOwner('');
                    }}
                  >
                    Cancel
                  </button>
                  <button
                    className="custom-tag-create"
                    onClick={handleAddCustomTag}
                    disabled={!customTagName.trim() || !customTagOwner.trim()}
                  >
                    Create & Drag
                  </button>
                </div>
              </div>
            )}
          </div>

          {draggedTag && draggedTag.isCustom && (
            <div className="drag-instruction">
              <p>Now drag your custom tag "{draggedTag.name}" onto the chart</p>
              <div
                className="draggable-tag custom"
                draggable
                onDragStart={(e) => handleDragStart(e, draggedTag)}
                onDragEnd={handleDragEnd}
              >
                <span className="tag-marker" style={{ background: draggedTag.color }}></span>
                {draggedTag.name}
                <span className="tag-owner">by {draggedTag.owner}</span>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default KPIChart;
