import React from 'react';
import './FilterBar.css';

function FilterBar({
  selectedPeriod,
  onPeriodChange,
  selectedUseCase,
  useCases,
  onUseCaseChange,
  businessSegment,
  onBusinessSegmentChange,
  deviceType,
  onDeviceTypeChange,
  pageType,
  onPageTypeChange,
  pageTypes = ['All']
}) {
  const periodOptions = [
    { value: '7', label: '7D' },
    { value: '30', label: '30D' },
    { value: '60', label: '60D' },
    { value: '90', label: '90D' },
    { value: 'all', label: 'All' }
  ];

  const businessSegmentOptions = [
    { value: 'All', label: 'All' },
    { value: 'B2B', label: 'B2B' },
    { value: 'B2C', label: 'B2C' }
  ];

  const deviceTypeOptions = [
    { value: 'All', label: 'All' },
    { value: 'MW', label: 'MW' },
    { value: 'DTW', label: 'DTW' },
    { value: 'App', label: 'App' }
  ];

  return (
    <div className="filter-bar">
      <div className="filter-bar-content">
        {/* Use Case Selector */}
        <div className="filter-group">
          <label className="filter-label">Use Case</label>
          <div className="filter-select-wrapper">
            <select
              value={selectedUseCase}
              onChange={(e) => onUseCaseChange(e.target.value)}
              className="filter-select"
            >
              {useCases.map(uc => (
                <option key={uc} value={uc}>{uc}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Period Selector */}
        <div className="filter-group">
          <label className="filter-label">Period</label>
          <div className="toggle-group">
            {periodOptions.map(option => (
              <button
                key={option.value}
                className={`toggle-btn ${selectedPeriod === option.value ? 'active' : ''}`}
                onClick={() => onPeriodChange(option.value)}
              >
                {option.label}
              </button>
            ))}
          </div>
        </div>

        {/* Business Segment Selector */}
        <div className="filter-group">
          <label className="filter-label">Business</label>
          <div className="toggle-group">
            {businessSegmentOptions.map(option => (
              <button
                key={option.value}
                className={`toggle-btn ${businessSegment === option.value ? 'active' : ''}`}
                onClick={() => onBusinessSegmentChange(option.value)}
              >
                {option.label}
              </button>
            ))}
          </div>
        </div>

        {/* Device Type Selector */}
        <div className="filter-group">
          <label className="filter-label">Device</label>
          <div className="toggle-group">
            {deviceTypeOptions.map(option => (
              <button
                key={option.value}
                className={`toggle-btn ${deviceType === option.value ? 'active' : ''}`}
                onClick={() => onDeviceTypeChange(option.value)}
              >
                {option.label}
              </button>
            ))}
          </div>
        </div>

        {/* Page Type Selector */}
        <div className="filter-group">
          <label className="filter-label">Page Type</label>
          <div className="filter-select-wrapper">
            <select
              value={pageType}
              onChange={(e) => onPageTypeChange(e.target.value)}
              className="filter-select"
            >
              {pageTypes.map(pt => (
                <option key={pt} value={pt}>{pt}</option>
              ))}
            </select>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FilterBar;
