import React from 'react';
import './AdvancedAnalysis.css';

function AdvancedAnalysis({ analysisData, useCases, selectedUseCase, onUseCaseChange }) {
  
  const formatValue = (value, kpi) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    
    const numValue = Number(value);
    
    if (kpi === 'REVENUE') {
      if (Math.abs(numValue) >= 1000000) {
        return `$${(numValue / 1000000).toFixed(2)}M`;
      }
      if (Math.abs(numValue) >= 1000) {
        return `$${(numValue / 1000).toFixed(1)}K`;
      }
      return `$${numValue.toFixed(0)}`;
    }
    if (kpi === 'AOV' || kpi === 'RPV') {
      return `$${numValue.toFixed(2)}`;
    }
    if (kpi === 'CVR') {
      return `${numValue.toFixed(2)}%`;
    }
    if (Math.abs(numValue) >= 1000000) {
      return `${(numValue / 1000000).toFixed(2)}M`;
    }
    if (Math.abs(numValue) >= 1000) {
      return `${(numValue / 1000).toFixed(1)}K`;
    }
    return numValue.toLocaleString();
  };

  const formatLift = (value) => {
    if (value === null || value === undefined || isNaN(value)) {
      return { text: '-', className: '' };
    }
    
    const numValue = Number(value);
    const sign = numValue > 0 ? '+' : '';
    const className = numValue > 0 ? 'positive' : numValue < 0 ? 'negative' : '';
    
    return {
      text: `${sign}${numValue.toFixed(2)}%`,
      className
    };
  };

  const launchInfo = analysisData.length > 0 ? {
    date: analysisData[0].launch_date,
    days: analysisData[0].post_days
  } : null;

  return (
    <div className="analysis-page">
      <div className="analysis-header">
        <div className="header-title">
          <h1>Advanced Analysis</h1>
          <p>Pre-launch vs Post-launch performance comparison</p>
        </div>
        
        <div className="analysis-controls">
          <div className="control-item">
            <label>Use Case</label>
            <select 
              value={selectedUseCase} 
              onChange={(e) => onUseCaseChange(e.target.value)}
            >
              {useCases.map(uc => (
                <option key={uc} value={uc}>{uc}</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {launchInfo && (
        <div className="launch-banner glass">
          <div className="banner-item">
            <span className="banner-label">Launch Date</span>
            <span className="banner-value">
              {new Date(launchInfo.date).toLocaleDateString('en-US', {
                month: 'long',
                day: 'numeric',
                year: 'numeric'
              })}
            </span>
          </div>
          <div className="banner-divider"></div>
          <div className="banner-item">
            <span className="banner-label">Days Since Launch</span>
            <span className="banner-value">{launchInfo.days}</span>
          </div>
        </div>
      )}

      {analysisData.length > 0 ? (
        <div className="analysis-table-wrapper glass-heavy">
          <div className="table-scroll">
            <table className="analysis-table">
              <thead>
                <tr>
                  <th className="sticky-col">KPI</th>
                  <th>Pre LY</th>
                  <th>Pre TY</th>
                  <th>Pre Lift</th>
                  <th>Post LY</th>
                  <th>Post TY</th>
                  <th>Post Lift</th>
                  <th className="highlight-col">Comp Lift</th>
                </tr>
              </thead>
              <tbody>
                {analysisData.map((row, index) => {
                  const preLift = formatLift(row.pre_lift);
                  const postLift = formatLift(row.post_lift);
                  const compLift = formatLift(row.pre_post_comp_lift);
                  
                  return (
                    <tr key={`${row.kpi}-${index}`}>
                      <td className="sticky-col kpi-name">{row.kpi}</td>
                      <td>{formatValue(row.pre_ly, row.kpi)}</td>
                      <td>{formatValue(row.pre_ty, row.kpi)}</td>
                      <td className={preLift.className}>{preLift.text}</td>
                      <td>{formatValue(row.post_ly, row.kpi)}</td>
                      <td>{formatValue(row.post_ty, row.kpi)}</td>
                      <td className={postLift.className}>{postLift.text}</td>
                      <td className={`highlight-col ${compLift.className}`}>
                        <span className="comp-value">{compLift.text}</span>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="empty-analysis glass-heavy">
          <p>No analysis data available</p>
        </div>
      )}

      <div className="analysis-legend glass">
        <h3>Metric Definitions</h3>
        <div className="legend-grid">
          <div className="legend-item">
            <span className="legend-term">Pre Period</span>
            <span className="legend-def">Same number of days before launch as days elapsed since launch</span>
          </div>
          <div className="legend-item">
            <span className="legend-term">Post Period</span>
            <span className="legend-def">From launch date to today</span>
          </div>
          <div className="legend-item">
            <span className="legend-term">LY / TY</span>
            <span className="legend-def">Last Year / This Year for the same date range</span>
          </div>
          <div className="legend-item">
            <span className="legend-term">Lift</span>
            <span className="legend-def">Percentage change: (TY - LY) / LY x 100</span>
          </div>
          <div className="legend-item">
            <span className="legend-term">Comp Lift</span>
            <span className="legend-def">Post Lift minus Pre Lift - measures incremental impact of launch</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdvancedAnalysis;
