import React from 'react';
import './MetricCard.css';

function MetricCard({ 
  label, 
  kpiKey,
  postLift, 
  postTY, 
  postLY, 
  preLift,
  preTY,
  preLY,
  compLift,
  format,
  isSelected,
  onSelect,
  onExpand
}) {
  const isPositive = postLift > 0;
  const isNegative = postLift < 0;

  const formatValue = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    
    if (format === 'currency') {
      if (Math.abs(value) >= 1000000) {
        return `$${(value / 1000000).toFixed(2)}M`;
      }
      if (Math.abs(value) >= 1000) {
        return `$${(value / 1000).toFixed(1)}K`;
      }
      return `$${value.toFixed(2)}`;
    }
    if (format === 'percentage') {
      return `${value.toFixed(2)}%`;
    }
    if (Math.abs(value) >= 1000000) {
      return `${(value / 1000000).toFixed(2)}M`;
    }
    if (Math.abs(value) >= 1000) {
      return `${(value / 1000).toFixed(1)}K`;
    }
    return value.toLocaleString();
  };

  const formatLift = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    const sign = value > 0 ? '+' : '';
    return `${sign}${value.toFixed(2)}%`;
  };

  const getArrow = () => {
    if (isPositive) return '↑';
    if (isNegative) return '↓';
    return '→';
  };

  const handleCardClick = () => {
    if (onSelect) {
      onSelect(kpiKey);
    }
  };

  const handleExpandClick = (e) => {
    e.stopPropagation();
    if (onExpand) {
      onExpand({
        label,
        kpiKey,
        postLift,
        postTY,
        postLY,
        preLift,
        preTY,
        preLY,
        compLift,
        format
      });
    }
  };

  return (
    <div 
      className={`metric-card 
        ${isPositive ? 'positive' : ''} 
        ${isNegative ? 'negative' : ''}
        ${isSelected ? 'selected' : ''}`}
      onClick={handleCardClick}
    >
      <div className="card-top">
        <span className="card-label">{label}</span>
        <button 
          className="info-btn"
          onClick={handleExpandClick}
          aria-label="View details"
        >
          <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <circle cx="12" cy="12" r="10"></circle>
            <line x1="12" y1="16" x2="12" y2="12"></line>
            <line x1="12" y1="8" x2="12.01" y2="8"></line>
          </svg>
        </button>
      </div>
      
      <div className="card-metric">
        <span className={`metric-arrow ${isPositive ? 'up' : ''} ${isNegative ? 'down' : ''}`}>
          {getArrow()}
        </span>
        <span className="metric-value">{formatLift(postLift)}</span>
      </div>

      <div className="card-comparison">
        <span className="comparison-ty">{formatValue(postTY)}</span>
        <span className="comparison-vs">vs</span>
        <span className="comparison-ly">{formatValue(postLY)}</span>
      </div>
    </div>
  );
}

export default MetricCard;
