import React from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from 'recharts';
import './DonutChart.css';

function DonutChart({ title, value, target, color, format }) {
  // Handle null/undefined values
  const safeValue = value || 0;
  const safeTarget = target || 1;
  
  const percentage = Math.min((safeValue / safeTarget) * 100, 100);
  
  const data = [
    { name: 'Achieved', value: percentage },
    { name: 'Remaining', value: Math.max(0, 100 - percentage) }
  ];

  const formatValue = (val) => {
    if (val === null || val === undefined || isNaN(val)) {
      return '-';
    }
    
    if (format === 'currency') {
      return new Intl.NumberFormat('en-US', { 
        style: 'currency', 
        currency: 'USD',
        minimumFractionDigits: 2,
        maximumFractionDigits: 2
      }).format(val);
    }
    if (format === 'percentage') {
      return `${val.toFixed(4)}%`;
    }
    if (format === 'decimal') {
      return val.toFixed(4);
    }
    return new Intl.NumberFormat('en-US').format(Math.round(val));
  };

  return (
    <div className="donut-chart-container glass-card">
      <h4 className="donut-title">{title}</h4>
      <div className="donut-wrapper">
        <ResponsiveContainer width="100%" height={120}>
          <PieChart>
            <Pie
              data={data}
              cx="50%"
              cy="50%"
              innerRadius={35}
              outerRadius={50}
              paddingAngle={2}
              dataKey="value"
              startAngle={90}
              endAngle={-270}
              stroke="none"
            >
              <Cell key="achieved" fill={color} />
              <Cell key="remaining" fill="rgba(0,0,0,0.08)" />
            </Pie>
            <Tooltip 
              formatter={(val) => `${val.toFixed(1)}%`}
              contentStyle={{
                background: 'rgba(255,255,255,0.95)',
                border: 'none',
                borderRadius: '8px',
                boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                fontSize: '12px'
              }}
            />
          </PieChart>
        </ResponsiveContainer>
        <div className="donut-center">
          <span className="donut-percentage">{percentage.toFixed(0)}%</span>
        </div>
      </div>
      <div className="donut-value">{formatValue(safeValue)}</div>
    </div>
  );
}

export default DonutChart;
