import React, { useEffect } from 'react';
import './MetricModal.css';

function MetricModal({ data, onClose }) {
  const {
    label,
    kpiKey,
    postLift,
    postTY,
    postLY,
    preLift,
    preTY,
    preLY,
    compLift,
    format,
    isBpsKpi
  } = data;

  // Determine if this is a BPS KPI (CVR)
  const useBps = isBpsKpi || kpiKey === 'cvr';

  const isPositive = (val) => val > 0;
  const isNegative = (val) => val < 0;

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') onClose();
    };
    document.addEventListener('keydown', handleEscape);
    document.body.style.overflow = 'hidden';
    
    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = '';
    };
  }, [onClose]);

  const formatValue = (value) => {
    if (value === null || value === undefined || isNaN(value)) return '-';
    
    if (format === 'currency') {
      if (Math.abs(value) >= 1000000) {
        return `$${(value / 1000000).toFixed(2)}M`;
      }
      if (Math.abs(value) >= 1000) {
        return `$${(value / 1000).toFixed(1)}K`;
      }
      return `$${value.toFixed(2)}`;
    }
    if (format === 'percentage') {
      // CVR comes as decimal (0.0225), display as percentage (2.25%)
      return `${(value * 100).toFixed(2)}%`;
    }
    if (Math.abs(value) >= 1000000) {
      return `${(value / 1000000).toFixed(2)}M`;
    }
    if (Math.abs(value) >= 1000) {
      return `${(value / 1000).toFixed(1)}K`;
    }
    return value.toLocaleString();
  };

  const formatLift = (value) => {
    if (value === null || value === undefined || isNaN(value)) {
      return useBps ? '0 bps' : '0%';
    }
    
    if (useBps) {
      // Value is already in BPS from backend
      const sign = value >= 0 ? '+' : '-';
      return `${sign}${Math.abs(value).toFixed(0)} bps`;
    }
    
    const sign = value >= 0 ? '+' : '-';
    return `${sign}${Math.abs(value).toFixed(2)}%`;
  };

  const formatLiftShort = (value) => {
    if (value === null || value === undefined || isNaN(value)) {
      return useBps ? '0 bps' : '0%';
    }
    
    if (useBps) {
      return `${Math.abs(value).toFixed(0)} bps`;
    }
    
    return `${Math.abs(value).toFixed(2)}%`;
  };

  const getLiftClass = (value) => {
    if (isPositive(value)) return 'positive';
    if (isNegative(value)) return 'negative';
    return '';
  };

  const getArrow = (value) => {
    if (isPositive(value)) return '↑';
    if (isNegative(value)) return '↓';
    return '–';
  };

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Generate explanation text based on KPI type
  const getExplanation = () => {
    if (useBps) {
      return `Difference between Post Lift (${formatLift(postLift)}) and Pre Lift (${formatLift(preLift)}). ` +
             `A positive value indicates the launch improved conversion rate beyond the existing trend.`;
    }
    return `Difference between Post Lift (${formatLift(postLift)}) and Pre Lift (${formatLift(preLift)}), ` +
           `measuring the change attributable to the launch.`;
  };

  return (
    <div className="modal-backdrop" onClick={handleBackdropClick}>
      <div className="modal-container">
        <div className="modal-header">
          <h2>{label}</h2>
          {useBps && <span className="modal-unit-badge">Basis Points</span>}
          <button className="modal-close" onClick={onClose}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <line x1="18" y1="6" x2="6" y2="18"></line>
              <line x1="6" y1="6" x2="18" y2="18"></line>
            </svg>
          </button>
        </div>

        <div className="modal-body">
          {/* Post Period */}
          <div className="period-section">
            <div className="period-header">
              <h3>Post-Launch</h3>
              <span className={`period-lift ${getLiftClass(postLift)}`}>
                {getArrow(postLift)} {formatLiftShort(postLift)}
              </span>
            </div>
            <div className="period-data">
              <div className="data-item">
                <span className="data-label">This Year</span>
                <span className="data-value">{formatValue(postTY)}</span>
              </div>
              <div className="data-item">
                <span className="data-label">Last Year</span>
                <span className="data-value muted">{formatValue(postLY)}</span>
              </div>
              {useBps && (
                <div className="data-item">
                  <span className="data-label">Change</span>
                  <span className={`data-value ${getLiftClass(postTY - postLY)}`}>
                    {((postTY - postLY) * 10000).toFixed(0)} bps
                  </span>
                </div>
              )}
            </div>
          </div>

          {/* Pre Period */}
          <div className="period-section">
            <div className="period-header">
              <h3>Pre-Launch</h3>
              <span className={`period-lift ${getLiftClass(preLift)}`}>
                {getArrow(preLift)} {formatLiftShort(preLift)}
              </span>
            </div>
            <div className="period-data">
              <div className="data-item">
                <span className="data-label">This Year</span>
                <span className="data-value">{formatValue(preTY)}</span>
              </div>
              <div className="data-item">
                <span className="data-label">Last Year</span>
                <span className="data-value muted">{formatValue(preLY)}</span>
              </div>
              {useBps && (
                <div className="data-item">
                  <span className="data-label">Change</span>
                  <span className={`data-value ${getLiftClass(preTY - preLY)}`}>
                    {((preTY - preLY) * 10000).toFixed(0)} bps
                  </span>
                </div>
              )}
            </div>
          </div>

          {/* Comp Lift */}
          <div className="comp-section">
            <div className="comp-header">
              <h3>Incremental Impact</h3>
            </div>
            <div className={`comp-result ${getLiftClass(compLift)}`}>
              <span className="comp-arrow">{getArrow(compLift)}</span>
              <span className="comp-value">{formatLiftShort(compLift)}</span>
            </div>
            <p className="comp-explanation">
              {getExplanation()}
            </p>
          </div>

          {/* BPS Legend for CVR */}
          {useBps && (
            <div className="bps-legend">
              <h4>Understanding Basis Points</h4>
              <ul>
                <li><strong>1 bps</strong> = 0.01 percentage points</li>
                <li><strong>100 bps</strong> = 1.00 percentage point</li>
                <li>Example: CVR from 2.00% to 2.25% = +25 bps</li>
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default MetricModal;
